	//--| UCI Methods |---------------------------------------- 
	
	func run() {
		
		// Load Book Asynchronously
		var bookQueue : DispatchQueue = DispatchQueue.global(qos: .default)
		bookQueue.async(execute: {
			() -> Void in self.loadBookActual()
			})
		
		// Start Background Thread for Processing Commands
		queue.async { [weak self] in
			while let line = readLine() {
				self?.handleCommand(line)
			}
		}
		
		// Keep main thread alive to listen for commands
		RunLoop.main.run()
	}
	
	
    private func handleCommand(_ command: String) {
        let trimmedCommand = command.trimmingCharacters(in: .whitespacesAndNewlines)
		let firstWord = trimmedCommand.split(separator:  " ").first!
		let words : [String] = getWords( inString:command )
		
		switch firstWord {
			
			// uci → uciok
			case "uci" : 
				sendUCIResponse()
			
			// isready → readyok
			case "isready" :
				while (numBookMoves < 10400) {} 	// wait until book loaded! (about 4secs)
				print("readyok")
				fflush(__stdoutp)
			
			case "ucinewgame" :  
				initFEN( theBoard: &gBoard, fen: "rnbqkbnr/pppppppp/8/8/8/8/PPPPPPPP/RNBQKBNR w KQkq -" )
			
			// position startpos moves e2e4
			case "position" :  
				setupPosition(UCIcmd: trimmedCommand)
							
			// go wtime 4000 btime 4000 movestogo 9
			case "go" : 
				processMove(UCIcmd: trimmedCommand)
			
			// Performance Tests
			case "goperft" : 
				if words.count > 1 {runPerft( depth: Int(words[1])! ) }
				else {runPerft( depth: 3 ) }
			
			// fn SetSearchAborted( _true )
			case "stop" : 
				shouldTerminate = true
			
			case "quit" : 
				exit(0)
				
			case "setoption" : 
				// setoption name Depth value 5
				if words[2] == "Depth" { gDepth = Int(words[4])! }
				
				/*
				// setoption name UCI_Chess960 type check default false
				if words[2] == "UCI_Chess960" {
					if words[4] == "true" { gFischer = true }
					if words[4] == "false" { gFischer = false } 
					}
				*/

			default: 
				// deal with default response here.. 🤷🏼‍♂️ 
				print("Unknown command: \(trimmedCommand)")
				fflush(__stdoutp)
				
			}
		}
	
	
