	// UCI setupPosition(UCIcmd: trimmedCommmmand)
	// parse strings like the following:
	// HIARCS: position startpos moves e2e4 d7d5 d2d4 b8c6 g1f3 g8f6 f1b5
	// STOCKFISH: position fen rnbqkbnr/pppppppp/8/8/8/8/PPPPPPPP/RNBQKBNR w KQkq - 0 1 moves(8) e2e4 c7c5 f2f4 g7g6 g2g4 (13)
	func setupPosition(UCIcmd: String) {
		//let moveWords = UCIcmd.split(separator: " ").dropFirst(3)
		//let words : [String] = UCIcmd.split(separator: " ")
		let words : [String] = getWords( inString:UCIcmd )
		let numWords = words.count
		var moveStr : String = ""
		
		switch words[1] {
			// position startpos moves e2e4
			case "startpos" : 
				initFEN(theBoard: &gBoard, fen: "rnbqkbnr/pppppppp/8/8/8/8/PPPPPPPP/RNBQKBNR w KQkq -")
				
			case "fen" : 
				if (numWords > 6) {
					let fenStr = words[2] + " " + words[3] + " " + words[4] + " " + words[5] + " " + words[6] + " " + words[7]
					initFEN( theBoard: &gBoard, fen: fenStr )
					} else {
					initFEN(theBoard: &gBoard, fen: "rnbqkbnr/pppppppp/8/8/8/8/PPPPPPPP/RNBQKBNR w KQkq -")
					}
			
			default : 
				initFEN(theBoard: &gBoard, fen: "rnbqkbnr/pppppppp/8/8/8/8/PPPPPPPP/RNBQKBNR w KQkq -")
			
			}
		
		// if we play out the game, we need to zero the chain
		// note: if we get a FEN we will need to disable useBook! (write the code!)
		gameChain = ""
		
		// play out the game provided (only if 'moves' provided!)
		if (words[1] == "startpos") && (numWords > 2) {
			let moveWords : [String] = getWords( inString:UCIcmd )
			for movePos in 3 ..< moveWords.count { 
				moveStr = moveWords[movePos]
				userMove(moveStr: moveStr)
				}
			}
		
		return
	}
	
	
	// UCI Process Move Asynchronously + Timer
	private func processMove(UCIcmd: String) {
		let words : [String] = getWords( inString:UCIcmd )
		let numWords = words.count
		
		// parse subCommands
		var movesToGo = numMovesAssumedForTimeControl
		var UCIdepth = gDepth
		var timeLimit = gTimerDefault   //7.0 secs
		var wtime : Double = gTimerDefault
		var btime : Double = gTimerDefault
		
		for n in 1 ..< numWords {
			switch words[n] {
				// HiArcs send BOTH wtime and btime and not just the relevant value. 
				// therefore have to figure out if we are black or white from moves. 
				case "wtime" : wtime = (Double( words[n+1] )! ) ?? gTimerDefault
				case "btime" : btime = (Double( words[n+1] )! ) ?? gTimerDefault
				case "movestogo" : movesToGo = Int( words[n+1] ) ?? numMovesAssumedForTimeControl
				case "movetime" : timeLimit = (Double( words[n+1] )! ) ?? gTimerDefault
				case "depth" : UCIdepth = Int( words[n+1] ) ?? 5; timeLimit = infiniteTime; movesToGo = 1
				default : timeLimit = gTimerDefault
				}
			}
		
		// Time Management Purves Recommended
		// timeLimit = timeLimit * 95 / 100 / Double(movesToGo)
		var theTime : Double = 0.0 
		switch gBoard.who2move {
			case -1 : theTime = btime
			case 1 : theTime = wtime
			default : gTimerDefault * 1000  //secs > msecs
			}
			
		timeLimit = theTime / 950 / Double(movesToGo)
		if (timeLimit < gTimerDefault) {timeLimit = gTimerDefault}
		gTimer = timeLimit
		//print("--gTimer: \(gTimer) theTime: \(theTime) 🐰 Down the Rabbit Hole--")
		
		// 🐰 Down the Rabbit Hole!! 
		shouldTerminate = false
		queue.async { [weak self] in 
			guard let self = self else { return }
			
			startTimer()
			while !self.shouldTerminate {
				
				startTime = CFAbsoluteTimeGetCurrent()
				
				let computerMoveStr = computerMove(theBoard: &gBoard, useBook: false)
				
				print("bestmove \(computerMoveStr)")
				fflush(__stdoutp)
				break
				}
			
			// quit asynchronous quoue
			}
			
		// end processMove()
		}
	
	
	// PERFT Performance Test: perft 5
	private func runPerft(depth: Int) {
		var isCorrect : String = "🛑 ✅"
		var resultStr : String = ""
		let correctNodes : [Int] = [0, 20, 400, 8902, 197281, 4865609, 119060324, 3195901860, 84998978956, 2439530234167, 69352859712417, 2097651003696806, 62854969236701747]
				
		if ((depth > 0) && (depth < 8)) {
			
			let startTime = CFAbsoluteTimeGetCurrent()			
			let perftNodes = perft(inBoard:&gBoard, depth:depth)
			let endTime = CFAbsoluteTimeGetCurrent()
			
			// Test against Known Correct PERFT Results
			if (perftNodes == correctNodes[depth]) { 
				isCorrect = "✅"
				} else {
				isCorrect = "🛑[should be: \(correctNodes[depth])]"
				}
			
			let numSecs : String = String(format: "%.2f", (endTime-startTime) )
			let nodesSec : String = String(format: "%.2f", ( Double(perftNodes) / (endTime-startTime) ) ) 
			let perftStr = "PERFT depth \(depth) nodes \(perftNodes)\(isCorrect) time \(numSecs) nps \(nodesSec)"
			//resultStr = logBoard(theBoard:&gBoard) + "\n" + perftStr
			resultStr = perftStr
			
			}
		else {
			resultStr = "ERROR: perft range must = [1-7]"
			}
		
		print (resultStr)
		fflush(__stdoutp)
		
		return
	}
	
	
	// Setup Asynchronous Timer
	private func startTimer() {
		let timer = DispatchSource.makeTimerSource(queue: queue)
		timer.schedule(deadline: .now() + self.gTimer)
		timer.setEventHandler { [weak self] in 
			self?.shouldTerminate = true
			//JRP: we should invalidate() wherever we set Terminate = true
			//print("** timer terminated **")
			}
		timer.activate()
	}
	
	
