	func loadBookActual()
	{
		// load TXT file
		//let path = Bundle.main.path(forResource: "peaBook", ofType: "txt")!
		//let contents : String = try! String(contentsOfFile: path, encoding: String.Encoding.utf8)
		//let lines : [String] = contents.components(separatedBy: "\n")
		//for n in 0..<lines.count { print("\(lines[n])") }
		
		// instead of TXT file, the whole thing is inline
		declareBook()
		let lines : [String] = gBook.components(separatedBy: "\n")
		
		var y, y2, z : Int
		var yWordLen : Int
		var lenA : Int = 0
		var mark : Int = 0
		var a : String = ""
		var parseWord : String = ""
		
		// global to the class > their content is read from peaBook.txt
		numBookMoves = 0
		bookTitle = [""]
		bookChain = [""]
		
		// LOAD the Book
		for line: String in lines {
			
			// line is not empty, not a comment, AND starts with "{"
			if (line.count != 0) && !(line as NSString).substring(with: NSRange(location: 0, length: 2)).isEqual("//") && (line as NSString).substring(with: NSRange(location: 0, length: 1)).isEqual("{") {
				
				// { title of book } determine location of MARK
				lenA = line.count - 2
				a = midString(theString:line, charIndex:2, range:lenA)
				
				// mark = where "}" is (and god help us if there is no closing bracket!!)
				for z in 1..<(lenA - 2) {
					if getChar(theString:a, charIndex:z) == "}" {
					//if midString(theString:a, charIndex:z, range:1) == "}" {
						mark = z
						}
					}
				
				let oneTitle = midString(theString:a, charIndex:0, range:mark - 1)
				let rawChain = midString(theString:a, charIndex:mark + 2, range:lenA - (mark + 2) )
				
				// strip move numbers to build chains against which to compare the gameChain.
				// this assumes NO SPACES between number.andMove (example: 1.d4 Nf6 2.Nf3 e6 3.c4 b6..)
				let words : [String] = getWords(inString:rawChain)
				let numEle : Int = words.count
				var wordMutants : [String] = words
				
				for y in 0..<numEle {
					for y2 in 0..<wordMutants[y].count {
						//if midString(theString:wordMutants[y], charIndex:y2, range:1) == "." {
						if getChar(theString:wordMutants[y], charIndex:y2) == "." {
						
							yWordLen = wordMutants[y].count
							parseWord = midString(theString:wordMutants[y], charIndex: y2 + 1, range: yWordLen - (y2 + 1) )
							//parseWord = (wordMutants[y] as NSString).substring(with: NSRange(location: y2 + 1, length: yWordLen - (y2 + 1)))
							wordMutants[y] = parseWord
							}
						}
					}
				
				// put humpty dumpty back together again into a fresh-squeezed oneChain
				var oneChain = ""
				for z in 0..<numEle {
					oneChain = oneChain.appending(wordMutants[z])
					oneChain = oneChain.appending(" ")
					}
				
				// assign cleaned-up tempChain to book & title
				bookTitle.insert(oneTitle, at: numBookMoves)
				bookChain.insert(oneChain, at: numBookMoves)
				numBookMoves += 1
				
				}
			}
		
		print("bookLoaded: \(numBookMoves) openings")
		return
	}
	
	
	func bookMove() -> String
	{
		var bookMove : String = "--"
		var z, n, n2, numMatches : Int
		var pickIndex : Int
		var matchBook : [String] = [""]
		var matchTitles : [String] = [""]
		var gameWords : [String] = [""]
		var bookWords : [String] = [""]
		var bookWordComp : String = ""
		var gameWordComp : String = ""
		var gameChainLen : Int = 0
		
		// first find all the opening lines that match (String)gameChain
		numMatches = 0
		gameChainLen = gameChain.count
		gameWords = getWords(inString:gameChain)
		n = gameWords.count - 1
		
		for z in 0..<numBookMoves {
			bookWords = getWords(inString: bookChain[z])
			n2 = bookWords.count - 1
			
			// there MUST be more words in opening than gameChain, or (n+1) would crash the pick index
			if (n2 > (n + 1)) && (gameChainLen > 2) && (bookChain[z].count > gameChainLen) {
				// then we should see if it isEqual:gameChain.length
				bookWordComp = (bookChain[z] as NSString).substring(with: NSRange(location: 0, length: gameChainLen - 1))
				gameWordComp = (gameChain as NSString).substring(with: NSRange(location: 0, length: gameChainLen - 1))
			
				if (bookWordComp == gameWordComp) {
					matchBook.insert(bookChain[z], at: numMatches)
					matchTitles.insert(bookTitle[z], at: numMatches)
					numMatches += 1
					}
				}
			
			// this should only occur the first time through
			if gameChain.count < 2 {
				// if the gameChain.length is <=1 then match everything
				matchBook.insert(bookChain[z], at: numMatches)
				matchTitles.insert(bookTitle[z], at: numMatches)
				//NSLog(@"Match Book: %@", [matchBook objectAtIndex:numMatches]);
				numMatches += 1
				}
			
			}
		
		print("Book Moves: \(numBookMoves) | Matches: \(numMatches)")
		var pickLine : String = ""
		var matchTitle : String = ""
		
		if numMatches == 1 {
			pickLine = matchBook[0]
			matchTitle = matchTitles[0]
			print("Pick Line | \(pickLine)")
			print("\(matchTitle)")
			//outputField.text = matchTitle
			//bookField.text = matchTitle
			}
		
		if numMatches > 1 {
			// random pick using arc4 crytographic randomalysis
			//pickIndex = arc4random() % (numMatches - 1)
			let numMatchesCG : CGFloat = CGFloat(numMatches) - 1
			pickIndex = Int(arc4random_uniform(UInt32(numMatchesCG))) + 1
			
			pickLine = matchBook[pickIndex]
			matchTitle = matchTitles[pickIndex]
			print("Pick Index: \(pickIndex) | \(pickLine)")
			print("\(matchTitle)")
			//bookField.text = matchTitle
			}
		
		if !pickLine.isEqual("") {
			var pickWords = self.getWords(inString: pickLine)
			bookMove = (pickWords[n] )
			}
		
		return bookMove
	}
	
	
