	func validMoves (theBoard: inout boardRecord, theSquare: Int) -> String
	{
		var numLegalMoves : Int = 0
		var moveList : [moveRecord]
		var movesLegal : [moveRecord] = []
		var moveString : String = ""
		
		// we must first test for gInCheck in order to return valid results!!
		gInCheck = isSideToMoveInCheck( theBoard:&theBoard )
		
		// generate pieceTree moves for the piece
		moveList = pieceTree (theBoard: &theBoard, theSquare: theSquare)
		let numMoves : Int = moveList.count
		
		for n in 0..<numMoves {
			let theMove = moveList[n]
			var testBoard = theBoard
			
			makeMove( theBoard:&testBoard, theMove:theMove )
			
			if ( isLegalPosition(theBoard: &testBoard) ) {
				movesLegal.append(theMove)
				numLegalMoves += 1
				}
			}
		
		// test if any moves exist before trying to sort and report
		if (movesLegal.count > 0) {
			sortMoves(theMoves: &movesLegal)
			moveString = moves2Str(theMoves:movesLegal)
			}
		else {
			moveString = "--"
			}
		
		return moveString
	}
	
	
