	func checkTest (theBoard: inout boardRecord) -> String
	{
		var checkReturn : String = ""
		
		// first question: are there any legal moves available?
		let legalMoves : [moveRecord] = generateLegalMoves( theBoard: &theBoard )
		let numMoves : Int = legalMoves.count
		
		// second question: are we in CHECK?
		let checkFlag : Bool = isSideToMoveInCheck( theBoard: &theBoard )
		
		// now that we know: i) numMoves, and ii) inCheck? -- we can return the status:
		if ((checkFlag == true) && (numMoves > 0)) {checkReturn = "CHECK"}
		if ((checkFlag == true) && (numMoves == 0)) {checkReturn = "MATE"}
		if ((checkFlag == false) && (numMoves == 0)) {checkReturn = "STALE"}
		
		return checkReturn
	}
	
	
	func quiescentEval(theBoard: inout boardRecord) -> Int
	{
		var quiScore : Int = -quiescenceSearch( theBoard: &theBoard, alpha:-infinity, beta:infinity, depth:gDepth )
		return quiScore
	}
	
	
	// NOTE! you must feed algeb2Std a board from BEFORE the move has been made!
	// as it can not disambiguate a move when the piece has already been moved.
	// thus, you typically call with something like:
	// moveDisp1 = fn move2Std( gMoveList(n), gameBoards(n-1) )
	
	func algeb2Std( theBoard: inout boardRecord, algebMove: String ) -> String
	{
		var StdMove : String = ""
		var buildStr : String = ""
		var pieceStr : String = ""
		var numMoves : Int
		var moves : [moveRecord] = []
		var theMove : moveRecord
		
		var Algeb1 : String = ""
		var Algeb2 : String = ""
		var toStr : String = ""
		var gPromoTemp : Bool
		var checkBoard : boardRecord
		
		var fromSquare : Int = algeb2fromSQ(algebMove:algebMove)
		var toSquare : Int = algeb2toSQ(algebMove:algebMove)
		var pieceType : Int = 0
		
		if (fromSquare>=0) && (fromSquare<64) {
			pieceType = theBoard.square![fromSquare]
			}
		
		// PIECE-TYPE PREFIX (but not if you're a Pawn!!)
		switch abs(pieceType) {
			case 0 : pieceStr = ""; break
			case 1 : pieceStr = ""; break
			case 2 : pieceStr = "B"; break
			case 3 : pieceStr = "N"; break
			case 4 : pieceStr = "R"; break
			case 5 : pieceStr = "Q"; break
			case 6 : pieceStr = "K"; break
			default : pieceStr = ""
			}
		
		buildStr = pieceStr
		
		// DISAMBIGUATION if necessary:
		// eg1 (Nbxc6): rnbnkb1r/p1p2ppp/2B5/4N3/4P1q1/1PN5/P1PP1PPP/R2QKB1R b KQkq  [b8c6]
		// eg2 (Rcxe8): 2r1N1r1/2pn1k2/Bp6/p2p2Pp/P1NP1p1P/1P3P2/8/2R3K1 b -
		// eg3 (Nbd7): rnbqkb1r/pp3p1p/3p1np1/2pP4/8/2N5/PP1NPPPP/R1BQKB1R b KQkq
		
		let algebMoveCount : Int = algebMove.count
		toStr = midString(theString:algebMove, charIndex: algebMoveCount-2, range: 2)
		moves = isTarget(toSquare: toSquare, inBoard: &theBoard, pieceType: pieceStr)
		numMoves = moves.count
		
		if ((numMoves > 1) && (abs(pieceType) > 1)) {
			
			Algeb1 = move2Algebraic(theMove: &moves[0])
			Algeb2 = move2Algebraic(theMove: &moves[1])
			print("Alegb1: \(Algeb1) Algeb2: \(Algeb2)")
			
			// FILE [A..D] is the same, thus disambig with RANK [1..6]
			let algeb1File : String = getChar(theString:Algeb1, charIndex:0)
			let algeb2File : String = getChar(theString:Algeb2, charIndex:0)
			if (algeb1File == algeb2File) {
				if (fromSquare == moves[0].fromSquare) { buildStr += getChar(theString:Algeb1, charIndex:1) }
				if (fromSquare == moves[1].fromSquare) { buildStr += getChar(theString:Algeb2, charIndex:1) }
				}
			else {
				// otherwise disambiguate with FILE [A..D]
				if (fromSquare == moves[0].fromSquare) { buildStr += getChar(theString:Algeb1, charIndex:0) }
				if (fromSquare == moves[1].fromSquare) { buildStr += getChar(theString:Algeb2, charIndex:0) }
				}
			}
		
		// CAPTURES indicated with 'x' (pawns always disambiguate with a FILE[A..H] )
		let capturedPiece : Int = theBoard.square![toSquare]
		if ( (abs(pieceType)) == 1 && (capturedPiece != 0) ) { buildStr = getChar(theString:algebMove, charIndex:0) + "x" }
		if ( (abs(pieceType)) > 1 && (capturedPiece != 0) ) { buildStr += "x" }
		
		// DESTINATION (only comes after all the above)
		buildStr += toStr
		
		// CASTLING (O-O or O-O-O)
		// BUG (fixed): e1g1 should NOT == O-O: R7/p2k4/1pn5/2p1p3/7r/8/P6P/4R2K w -
		if ( ((algebMove=="e1g1") || (algebMove=="e8g8")) && (abs((theBoard.square?[fromSquare])!) == 6) ) { buildStr = "O-O" }
		if ( ((algebMove=="e1c1") || (algebMove=="e8c8")) && (abs((theBoard.square?[fromSquare])!) == 6) ) { buildStr = "O-O-O" }
		
		// FISCHER Castling
		if (theBoard.fischer!==true) && (abs((theBoard.square?[fromSquare])!) == 6) &&
			(abs((theBoard.square?[toSquare])!) == 4) &&
			( (toSquare == theBoard.rookSq1!) || (toSquare == theBoard.rookSq1!+56) )
				{ buildStr = "O-O-O" }
		if (theBoard.fischer!==true) && (abs((theBoard.square?[fromSquare])!) == 6) &&
			(abs((theBoard.square?[toSquare])!) == 4) &&
			( (toSquare == theBoard.rookSq2!) || (toSquare == theBoard.rookSq2!+56) )
				{ buildStr = "O-O" }
		
		// CHECK TEST (check/mate/stale)
		// disambiguation requires board BEFORE the move
		// check testing requires board AFTER the move
		checkBoard = theBoard
		gPromoTemp = gPromotion
		gPromotion = false
		
		// setup the move for testing
		let testMove = moveRecord( fromSquare: fromSquare, toSquare: toSquare, capturedPiece: capturedPiece)
		
		makeMove ( theBoard: &checkBoard, theMove: testMove )
		
		// PROMOTION (suffix pawn-queen promotion)
		if (gPromotion) {buildStr += "=q"}
		gPromotion = gPromoTemp
		
		// CHECK (suffix check/mate/stale)
		let checkReturn : String = checkTest( theBoard: &checkBoard )
		if (checkReturn == "CHECK") {buildStr += "+"}
		if (checkReturn == "MATE") {buildStr += "#"}
		if (checkReturn == "STALE") {buildStr += "-"}
		
		StdMove = buildStr
		
		//print("algebMove: \(algebMove) = StdMove: \(StdMove)")
		
		//string algebraicReborn = Std2Algebraic( StdMove, theBoard );
		//cout << "--| algebMove:" << algebMove << " | StdMove:" << StdMove << " | AlgebReborn: " << algebraicReborn << " |-----" << endl;
		//return StdMove + " | " + algebraicReborn;
		
		return StdMove
	}
	
	
	func Std2Algebraic( inBoard: inout boardRecord, stdMove: String ) -> String
	{
		var theBoard : boardRecord = inBoard
		var STDstr : String = stdMove
		var pieceType : String = ""
		var algebMove : String = ""
		var fromStr : String = ""
		var toStr : String = ""
		var strLength : Int = 0
		var numMoves : Int = 0
		var querySquare : Int
		var moves : [moveRecord] = []
		var disAmbig : String = ""
		var Algeb1 : String = ""
		var Algeb2 : String = ""
		
		// FEN: r2q1rk1/pp2ppbp/1np2np1/2Q3B1/3PP1b1/2N2N2/PP3PPP/3RKB1R w -
		
		// Trap Castling Moves (Classic Only!!)
		if ( (STDstr == "O-O") && (theBoard.who2move == 1) && (!theBoard.fischer!) ) {
			algebMove = "e1g1"
			return algebMove
			}
		if ( (STDstr == "O-O") && (theBoard.who2move == -1) && (!theBoard.fischer!) ) {
			algebMove="e8g8"
			return algebMove
			}
		
		if ( (STDstr == "O-O-O") && (theBoard.who2move == 1) && (!theBoard.fischer!) ) {
			algebMove="e1c1"
			return algebMove
			}
		if ( (STDstr == "O-O-O") && (theBoard.who2move == -1) && (!theBoard.fischer!) ) {
			algebMove="e8c8"
			return algebMove
			}
		
		// Remove Check Suffixes
		var strEnd : Int = STDstr.count
		for z in stride(from:STDstr.count, to:0, by:-1) {
			let suffixChar : String = getChar(theString:STDstr, charIndex:z-1)
			if ( (suffixChar == "+") || (suffixChar == "#") ) {
				strEnd -= 1
				}
			}
		
		STDstr = midString(theString:STDstr, charIndex:0, range:strEnd)
		
		// Remove Pawn Promotion Suffix
		strEnd = STDstr.count
		let secondLastChar : String = getChar(theString:STDstr, charIndex:strEnd-2)
		if (secondLastChar == "=") {
			STDstr = midString(theString:STDstr, charIndex:0, range:strEnd-2)
			}
		
		// toSTRING > strip it all down to the last two chars
		strEnd = STDstr.count
		toStr = midString(theString:STDstr, charIndex:strEnd-2, range:2)
		
		// Simple Pawn Move (just a TO square)
		strEnd = STDstr.count
		if (strEnd == 2) {
			pieceType = "P"
			querySquare = algeb2fromSQ(algebMove:toStr)
			moves = isTarget(toSquare: querySquare, inBoard: &theBoard, pieceType: pieceType)
			numMoves = moves.count
			
			if (numMoves==1) {
				algebMove = move2Algebraic( theMove:&moves[0] )
				return algebMove
				}
			}
		
		// Prefix Pawn Moves for disambiguation
		// should translate moves: axb5, dxc4 --to-be-> Paxb5, Pdxc4
		let a : String = getChar(theString:STDstr, charIndex:0)
		let b : String = getChar(theString:STDstr, charIndex:1)
		let characterset = CharacterSet(charactersIn: "abcdefgh")
		if (a.rangeOfCharacter(from: characterset.inverted) == nil) && (b == "x") { STDstr = "P" + STDstr }
		
		strEnd = STDstr.count
		print("STDstr: \(STDstr) len:\(strEnd) numMoves:\(numMoves)")
		
		// disambiguate moves
		if (strEnd > 2) {
			pieceType = getChar(theString:STDstr, charIndex:0)
			querySquare = algeb2fromSQ(algebMove: toStr)
			moves = isTarget(toSquare: querySquare, inBoard: &theBoard, pieceType: pieceType)
			numMoves = moves.count
			print("pieceType: \(pieceType) querySQ: \(querySquare) numMoves: \(numMoves)")
			
			if (numMoves == 1) {
				algebMove = move2Algebraic( theMove: &moves[0] )
				return algebMove
				}
			
			// requires disambiguation
			if (numMoves == 2) {
				
				// relies on the fact that it will be there if there is > 1 move!!
				let disAmbig : String = getChar(theString:STDstr, charIndex:1)
				Algeb1 = move2Algebraic( theMove:&moves[0] )
				Algeb2 = move2Algebraic( theMove:&moves[1] )
				print("--> Algeb1: \(Algeb1) | Algeb2: \(Algeb2) <--")
				
				// disAmbig character is a FILE [a..h], thus disambig with RANK [1..8]
				var characterset = CharacterSet(charactersIn: "abcdefgh")
				if (disAmbig.rangeOfCharacter(from: characterset.inverted) == nil) {
					let algeb1_0 : String = getChar(theString: Algeb1, charIndex:0)
					let algeb2_0 : String = getChar(theString: Algeb2, charIndex:0)
					if (disAmbig == algeb1_0) {algebMove = Algeb1}
					if (disAmbig == algeb2_0) {algebMove = Algeb2}
					}
				
				// disambig character is a RANK [1..8]
				characterset = CharacterSet(charactersIn: "12345678")
				if (disAmbig.rangeOfCharacter(from: characterset.inverted) == nil) {
					let algeb1_1 : String = getChar(theString: Algeb1, charIndex:1)
					let algeb2_1 : String = getChar(theString: Algeb2, charIndex:1)
					if (disAmbig == algeb1_1) {algebMove = Algeb1}
					if (disAmbig == algeb2_1) {algebMove = Algeb2}
					}
				
				}
			}
		
		// error-trap if things go wrong.
		if (numMoves == 0) { algebMove = "--" }
		
		return algebMove
	}
	
	
	func initFEN( theBoard: inout boardRecord, fen:String )
	{
		var x, y, z : Int
		var n : Int
		
		//print("Engine FEN: \(fen)")
		
		let myWords : [String] = getWords( inString:fen )
		let numWords = myWords.count
		
		if (numWords != 0) {
			
			//Fischer FEN detection variables
			var wKingSq : Int = noSuchSquare
			var bKingSq : Int = noSuchSquare
			var wRookCounter : Int = 0
			var bRookCounter : Int = 0
			var wRook : [Int] = [99,99]
			var bRook : [Int] = [99,99]
			
			// initialize game states
			gPromotion = false;
			//gameFEN = fen;
			//gFischer = true;
			
			// clear out first move
			//gMoveList.fromSquare[0] = noSuchSquare;
			
			theBoard.who2move = 1
			theBoard.halfmoves = 0
			theBoard.enPassantSq = noSuchSquare
			
			theBoard.wCastleKside = false     // cant castle unless FEN allows it
			theBoard.wCastleQside = false
			theBoard.wHasCastled = true
			
			theBoard.bCastleKside = false
			theBoard.bCastleQside = false
			theBoard.bHasCastled = true
			
			theBoard.fischer = false
			theBoard.rookSq1 = noSuchSquare
			theBoard.rookSq2 = noSuchSquare
			
			// Temp FEN variables
			var a : String = ""
			var buildFEN : String = ""
			var valA : Int
			var colour2Move : String = ""
			var kq : String = ""
			
			
		// Expand myWords[0] into the raw FEN
			//int fenLen = myWords[0].length
			let fenLen : Int = myWords[0].count
			for z in 0..<fenLen {
			
				// only convert a to valA if a == "0123456789"
				a = getChar(theString:myWords[0], charIndex:z)
				valA = 0; let characterset = CharacterSet(charactersIn: "0123456789")
				if a.rangeOfCharacter(from: characterset.inverted) == nil {valA = Int(a)!}
				
				// expand numbers to dashes (e.g. '5' becomes "-----")
				if (valA > 0) {
					for bCount in 0..<valA {
						buildFEN += "-"
						}
					}
				
				// otherwise just append the character
				if !(valA > 0) {buildFEN += a}
				
				}
			
			// catch invalid FEN constructs (should = 71)
			if !(buildFEN.count==71) {print("FEN Error: \(buildFEN.count)/71 ")}
			
			// drop the raw FEN into the boardRecord
			var z : Int = 0
			
			for y in stride(from:8, to:0, by:-1) {
				for x in 1...8 {
					
					a = getChar(theString:buildFEN, charIndex:z)
					n = ( y*8 - (9-x) )
					
					if a=="-" {theBoard.square?[n] = 0}
					if a=="P" {theBoard.square?[n] = 1}
					if a=="B" {theBoard.square?[n] = 2}
					if a=="N" {theBoard.square?[n] = 3}
					if a=="R" {theBoard.square?[n] = 4; if n<8 && wRookCounter<2 {wRook[wRookCounter] = n; wRookCounter += 1} }
					if a=="Q" {theBoard.square?[n] = 5}
					if a=="K" {theBoard.square?[n] = 6; wKingSq = n}
					
					if a=="p" {theBoard.square?[n] = -1}
					if a=="b" {theBoard.square?[n] = -2}
					if a=="n" {theBoard.square?[n] = -3}
					if a=="r" {theBoard.square?[n] = -4; if n>55 && bRookCounter<2 {bRook[bRookCounter] = n; bRookCounter += 1} }
					if a=="q" {theBoard.square?[n] = -5}
					if a=="k" {theBoard.square?[n] = -6; bKingSq = n}
					
					z += 1
					}
				z += 1
				}
			
			// FISCHER: use above info to find FISCHER rookSq1 + rookSq2
			if ((wRookCounter > 0) && (wRook[0] < wKingSq)) { theBoard.rookSq1 = wRook[0] } else { theBoard.rookSq2 = wRook[0] }
			if (wRookCounter > 1) {
				if (wRook[wRookCounter-1] > wKingSq) { theBoard.rookSq2 = wRook[wRookCounter-1] } else { theBoard.rookSq1 = wRook[wRookCounter-1] }
				}
			
			if ((bRookCounter > 0) && (bRook[0] < bKingSq)) { theBoard.rookSq1 = bRook[0]-56 } else { theBoard.rookSq2 = bRook[0]-56 }
			if (bRookCounter > 1) {
				if (bRook[bRookCounter-1] > bKingSq) { theBoard.rookSq2 = bRook[bRookCounter-1]-56 } else { theBoard.rookSq1 = bRook[bRookCounter-1]-56 }
				 }
			
			// FISCHER or Classic?
			if (theBoard.square?[0] == 4 && theBoard.square?[4] == 6 && theBoard.square?[7] == 4 && theBoard.square?[56] == -4 && theBoard.square?[60] == -6 && theBoard.square?[63] == -4) {theBoard.fischer? = false}
			
			
			// set colour to move
			if (numWords >= 1) {
				colour2Move = myWords[1]
				if ( colour2Move == "w" || colour2Move == "W" ) {theBoard.who2move = 1}
				if ( colour2Move == "b" || colour2Move == "B" ) {theBoard.who2move = -1}
				}
			

			// set castling options
			if (numWords >= 2) {
				
				kq = myWords[2]
				
				// no castling
				if (kq=="-") {
					theBoard.wCastleKside = false
					theBoard.wCastleQside = false
					theBoard.bCastleKside = false
					theBoard.bCastleQside = false
					theBoard.wHasCastled = true
					theBoard.bHasCastled = true
					}
				
				// Classic castling column identifiers
				var wRookAside : String = "Q"
				var wRookHside : String = "K"
				var bRookAside : String = "q"
				var bRookHside : String = "k"
				
				// Determine IF any CHAR in kq is NOT 'KQkq' then set FISCHER = TRUE
				let kqLength : Int = kq.count
				if (kqLength > 0) {
					for z in 0..<kqLength {
						a = getChar(theString:kq, charIndex:z)
						
						// FISCHER if any of a is NOT "KQkq"
						let characterset = CharacterSet(charactersIn: "KQkq-")
						if a.rangeOfCharacter(from: characterset.inverted) != nil {	theBoard.fischer=true }
						}
					}
				
				// FISCHER castling column identifiers
				// rook squares relative to king determined from FEN
				// FEN: bqnbrnkr/pppppppp/8/8/8/8/PPPPPPPP/BQNBRNKR w Eh -
				if (theBoard.fischer == true) {
					if theBoard.rookSq1! != noSuchSquare { wRookAside = getChar(theString:"ABCDEFGH", charIndex:theBoard.rookSq1!) }
					if theBoard.rookSq2! != noSuchSquare { wRookHside = getChar(theString:"ABCDEFGH", charIndex:theBoard.rookSq2!) }
					if theBoard.rookSq1! != noSuchSquare { bRookAside = getChar(theString:"abcdefgh", charIndex:theBoard.rookSq1!) }
					if theBoard.rookSq2! != noSuchSquare { bRookHside = getChar(theString:"abcdefgh", charIndex:theBoard.rookSq2!) }
					}
				else {
					theBoard.rookSq1 = noSuchSquare
					theBoard.rookSq2 = noSuchSquare
					}
				
				// set castling as compared to castling column identifiers
				if (kqLength > 0) {
					for z in 0..<kqLength {
						a = getChar(theString:kq, charIndex:z)
						if a == wRookAside {theBoard.wCastleQside = true; theBoard.wHasCastled = false}
						if a == wRookHside {theBoard.wCastleKside = true; theBoard.wHasCastled = false}
						if a == bRookAside {theBoard.bCastleQside = true; theBoard.bHasCastled = false}
						if a == bRookHside {theBoard.bCastleKside = true; theBoard.bHasCastled = false}
						}
					}
				
				} //end numWords>=2; castling options
			
			
			// decode enPassant square
			// For example, Pawn at c4 should have 2 possible moves:
			// 8/7p/p5pb/4k3/P1pPn3/8/P5PP/1rB2RK1 b - d3
			if (numWords > 3) {
				if (myWords[3]=="-") {theBoard.enPassantSq = noSuchSquare}
				
				if (myWords[3]=="a3") {theBoard.enPassantSq = 24}
				if (myWords[3]=="b3") {theBoard.enPassantSq = 25}
				if (myWords[3]=="c3") {theBoard.enPassantSq = 26}
				if (myWords[3]=="d3") {theBoard.enPassantSq = 27}
				if (myWords[3]=="e3") {theBoard.enPassantSq = 28}
				if (myWords[3]=="f3") {theBoard.enPassantSq = 29}
				if (myWords[3]=="g3") {theBoard.enPassantSq = 30}
				if (myWords[3]=="h3") {theBoard.enPassantSq = 31}
				
				if (myWords[3]=="a6") {theBoard.enPassantSq = 32}
				if (myWords[3]=="b6") {theBoard.enPassantSq = 33}
				if (myWords[3]=="c6") {theBoard.enPassantSq = 34}
				if (myWords[3]=="d6") {theBoard.enPassantSq = 35}
				if (myWords[3]=="e6") {theBoard.enPassantSq = 36}
				if (myWords[3]=="f6") {theBoard.enPassantSq = 37}
				if (myWords[3]=="g6") {theBoard.enPassantSq = 38}
				if (myWords[3]=="h6") {theBoard.enPassantSq = 39}
				}
			
			
			// make initial entry into history and display
			//gMoveCount = 0 : gDispCount = 0
			//gameBoards(gMoveCount) = gBoard
			
			// remember to set the gameFEN if there was one
			//gGame.gameFEN = fen
			//gGame.variant = "[Variant " + chr$(34) + "fischerandom" + chr$(34) + "]"


		}  //end of numWords != 0
		
		return
	}
	
	
	
