	func getChar(theString:String, charIndex:Int) -> String
	{
		//swift3
		if theString.count > charIndex {
			return "\(theString[theString.index(theString.startIndex, offsetBy: charIndex)])"
			}
		return ""
	}
	
	func midString(theString:String, charIndex:Int, range:Int) -> String
	{
		// dumb ol for loop (to deal with variable unicoded characters)
		var buildStr : String = ""
			for n in charIndex ..< charIndex+range { buildStr += getChar(theString:theString, charIndex:n) }
		return buildStr
		
		// swift with NSRange
		//return (theString as NSString).substring(with: NSRange(location: charIndex, length: range))
		
		// swift4
		let start = theString.index(theString.startIndex, offsetBy: charIndex)
		let end = theString.index(theString.startIndex, offsetBy: charIndex+range)
		let span = start..<end
		return String(theString[span])
	}
	
	
