	func algeb2fromSQ(algebMove:String) -> Int
	{
		let colStr : String = getChar(theString:algebMove, charIndex:0)
		let rowStr : String = getChar(theString:algebMove, charIndex:1)
				
		let col : Int = col2int( col:colStr )
		let row : Int = row2int( row:rowStr )
		let fromSQ : Int = (col-1) + (row-1) * 8
		
		return fromSQ
	}
	
	
	func algeb2toSQ(algebMove:String) -> Int
	{
		let colStr : String = getChar(theString:algebMove, charIndex:2)
		let rowStr : String = getChar(theString:algebMove, charIndex:3)
		
		let col : Int = col2int( col:colStr )
		let row : Int = row2int( row:rowStr )
		let toSQ : Int = (col-1) + (row-1) * 8
		
		return toSQ
	}
	
	
	func col2int( col:String ) -> Int
	{
		var n : Int = 0
		if col == "a" { n = 1 }
		if col == "b" { n = 2 }
		if col == "c" { n = 3 }
		if col == "d" { n = 4 }
		if col == "e" { n = 5 }
		if col == "f" { n = 6 }
		if col == "g" { n = 7 }
		if col == "h" { n = 8 }
		return n;
	}
	
	
	func row2int( row:String ) -> Int
	{
		var n : Int = 0
		if row == "1" { n = 1 }
		if row == "2" { n = 2 }
		if row == "3" { n = 3 }
		if row == "4" { n = 4 }
		if row == "5" { n = 5 }
		if row == "6" { n = 6 }
		if row == "7" { n = 7 }
		if row == "8" { n = 8 }
		return n;
	}
	
	
	// pChess Report Moves (uses alphaRow + alphaCol)
	func move2Algebraic(theMove: inout moveRecord) -> String
	{
		var moveStr : String = ""
		var fromX, fromY : Int
		var toX, toY : Int
		
		fromX = sq2X(square: theMove.fromSquare!) + 1
		fromY = sq2Y(square: theMove.fromSquare!) + 1
		toX = sq2X(square: theMove.toSquare!) + 1
		toY = sq2Y(square: theMove.toSquare!) + 1
		
		moveStr = alphaCol(column:fromX)
		moveStr += alphaRow(row:fromY)
		moveStr += alphaCol(column:toX)
		moveStr += alphaRow(row:toY)
		
		return moveStr
	}
	
	func alphaCol( column: Int ) -> String
	{
		var alphaCol : String = ""
		
		if column == 1 {alphaCol = "a"}
		if column == 2 {alphaCol = "b"}
		if column == 3 {alphaCol = "c"}
		if column == 4 {alphaCol = "d"}
		if column == 5 {alphaCol = "e"}
		if column == 6 {alphaCol = "f"}
		if column == 7 {alphaCol = "g"}
		if column == 8 {alphaCol = "h"}
		
		return alphaCol
	}
	
	func alphaRow( row: Int ) -> String
