	func moveSQ2algebraic( moveSq: Int ) -> String
	{
		// lookup can be replaced with grabbing from an index!
		var algeb : String = "--"
		var AL : String = "a1b1c1d1e1f1g1h1" + "a2b2c2d2e2f2g2h2"
			AL += "a3b3c3d3e3f3g3h3" + "a4b4c4d4e4f4g4h4"
			AL += "a5b5c5d5e5f5g5h5" + "a6b6c6d6e6f6g6h6"
			AL += "a7b7c7d7e7f7g7h7" + "a8b8c8d8e8f8g8h8"
		
		if ((moveSq >= 0) && (moveSq <= 63)) {
			algeb = midString(theString:AL, charIndex:moveSq*2, range:2)
			}
		
		return algeb
	}
	
	
	func str2SQ( algebMove: String ) -> Int
	{
		let colStr : String = getChar(theString:algebMove, charIndex:0)
		let rowStr : String = getChar(theString:algebMove, charIndex:1)
		let col : Int = col2int( col:colStr )
		let row : Int = row2int( row:rowStr )
		let querySquare : Int = (col-1) + (row-1) * 8
		
		return querySquare
	}


	func move2Str( theMove:moveRecord ) -> String
	{
		let buildStr : String = moveSQ2algebraic( moveSq:theMove.fromSquare! ) + moveSQ2algebraic( moveSq:theMove.toSquare! )
		return buildStr
	}
	
	
	func moves2Str( theMoves:[moveRecord] ) -> String
	{
		var buildStr : String = ""
		let numLegalMoves = theMoves.count
		
		// generate moves string from array of returned moves
		if numLegalMoves > 0 {
			for n in 0..<numLegalMoves {
				let theMove : moveRecord = theMoves[n]
				buildStr = buildStr + " " + moveSQ2algebraic( moveSq:theMove.fromSquare! ) + moveSQ2algebraic( moveSq:theMove.toSquare! )
				}
			}
		
		// timming whitespace
		buildStr = buildStr.trimmingCharacters(in: CharacterSet.whitespaces)
		
		return buildStr
	}
	
	
