	func sq2X(square:Int) -> Int
	{
		return square % 8
	}
	
	
	func sq2Y(square:Int) -> Int
	{
		let y : Int
		if (square<0) {y = -1} else {y = Int(square/8) }
		return y
	}
	
	
	func wPawnMoves( theBoard : inout boardRecord, querySquare:Int, numMoves:Int) -> [moveRecord]
	{
		var pawnMoves : [moveRecord] = []
		var moveIndex : Int = 0
		var pieceMoves : Int = 0
		let fromY : Int = sq2Y(square:querySquare)
		
		var testMask : Int
		var newMask : Int
		var testSquare : Int
		
		// pawn NW (captures)
		testMask = sq2Mask[querySquare]
		newMask = testMask + wPawnMask[0]
		testSquare = querySquare + wPawnVect[0]
		
		if (gMaskBoard[newMask] == 1) {
			if (theBoard.square![testSquare] < 0) {
				moveIndex = numMoves + pieceMoves
				let theMove = moveRecord(fromSquare:querySquare, toSquare:testSquare, capturedPiece:theBoard.square![testSquare])
				pawnMoves.append(theMove)
				pieceMoves += 1
				}
			}
		
		// pawn NE (captures)
		testMask = sq2Mask[querySquare]
		newMask = testMask + wPawnMask[1]
		testSquare = querySquare + wPawnVect[1]
		
		if (gMaskBoard[newMask] == 1) {
			if (theBoard.square![testSquare] < 0) {
				moveIndex = numMoves + pieceMoves
				let theMove = moveRecord(fromSquare:querySquare, toSquare:testSquare, capturedPiece:theBoard.square![testSquare])
				pawnMoves.append(theMove)
				pieceMoves += 1
				}
			}
		
		// pawn forward 1x (space)
		testMask = sq2Mask[querySquare]
		newMask = testMask + wPawnMask[2]
		testSquare = querySquare + wPawnVect[2]
		
		if (gMaskBoard[newMask] == 1) {
			if (theBoard.square![testSquare] == 0) {
				moveIndex = numMoves + pieceMoves
				let theMove = moveRecord(fromSquare:querySquare, toSquare:testSquare, capturedPiece:0)
				pawnMoves.append(theMove)
				pieceMoves += 1
				}
			}
		
		// pawn forward 2x (space)
		testMask = sq2Mask[querySquare]
		newMask = testMask + wPawnMask[3]
		testSquare = querySquare + wPawnVect[3]
		
		if (gMaskBoard[newMask] == 1) {
			if ( querySquare > 7) && (querySquare < 16) && (theBoard.square![testSquare]==0) && (theBoard.square![testSquare-8]==0) {
				moveIndex = numMoves + pieceMoves
				let theMove = moveRecord(fromSquare:querySquare, toSquare:testSquare, capturedPiece:0)
				pawnMoves.append(theMove)
				pieceMoves += 1
				}
			}
		
		// pawn enPassant-West (captures)
		testMask = sq2Mask[querySquare]
		newMask = testMask + wPawnMask[4]
		testSquare = querySquare + wPawnVect[4]
		
		if (gMaskBoard[newMask] == 1) {
			if (fromY == 4) && (testSquare == theBoard.enPassantSq!) {
				moveIndex = numMoves + pieceMoves
				let theMove = moveRecord(fromSquare:querySquare, toSquare:querySquare+7, capturedPiece:theBoard.square![testSquare])  //legal because Y=4 and enPasSq=onBoard
				pawnMoves.append(theMove)
				pieceMoves += 1
				}
			}
		
		// pawn enPasant-East (captures)
		testMask = sq2Mask[querySquare]
		newMask = testMask + wPawnMask[5]
		testSquare = querySquare + wPawnVect[5]
		
		if (gMaskBoard[newMask] == 1) {
			if (fromY == 4) && (testSquare == theBoard.enPassantSq!) {
				moveIndex = numMoves + pieceMoves
				let theMove = moveRecord(fromSquare:querySquare, toSquare:querySquare+9, capturedPiece:theBoard.square![testSquare])  //legal because Y=4 and enPasSq=onBoard
				pawnMoves.append(theMove)
				pieceMoves += 1
				}
			}
		
		return pawnMoves
	}


