					for n in stride(from:kingSq, through:theBoard.rookSq1!+56, by:-1) {
						if ( isAttacked(theBoard: &theBoard, querySquare: n) ) {bQside = false; break}
						}
					
					// generate move entry
					if (bQside) {
						moveIndex = numMoves + pieceMoves
						let theMove = moveRecord(fromSquare:querySquare, toSquare:theBoard.rookSq1!+56, capturedPiece:0)
						kingMoves.append(theMove)
						pieceMoves += 1
						}
					
					// restore order to the universe
					for n in stride(from:kingSq, through:theBoard.rookSq1!+56, by:-1) {
						theBoard.square?[n] = holder[n-56]
						}
					theBoard.who2move = -theBoard.who2move
					
					}  //end of 'we made it'
				
				}  //end of bCastleQside
			
			}  //end of (!gInCheck) && (theBoard.fischer)
		
		return kingMoves
	}
	
	
	func pieceTree(theBoard: inout boardRecord, theSquare: Int) -> [moveRecord]
	{
		var pieceMoves : [moveRecord]
		var numMoves : Int = 0
		var pieceType : Int
		var pieceColour : Int
		
		pieceType = theBoard.square![theSquare]
		if (pieceType > 0) {pieceColour = 1} else {pieceColour = -1}
		
		switch pieceType {
			case  1 : return wPawnMoves( theBoard:&theBoard, querySquare: theSquare, numMoves:numMoves)
			case -1 : return bPawnMoves( theBoard:&theBoard, querySquare: theSquare, numMoves:numMoves)
			case  2 : return bishopMoves( theBoard:&theBoard, querySquare:theSquare, numMoves:numMoves, pieceColour:pieceColour )
			case -2 : return bishopMoves( theBoard:&theBoard, querySquare:theSquare, numMoves:numMoves, pieceColour:pieceColour )
			case  3 : return knightMoves( theBoard:&theBoard, querySquare:theSquare, numMoves:numMoves, pieceColour:pieceColour )
			case -3 : return knightMoves( theBoard:&theBoard, querySquare:theSquare, numMoves:numMoves, pieceColour:pieceColour )
			case  4 : return rookMoves( theBoard:&theBoard, querySquare:theSquare, numMoves:numMoves, pieceColour:pieceColour )
			case -4 : return rookMoves( theBoard:&theBoard, querySquare:theSquare, numMoves:numMoves, pieceColour:pieceColour )
			case  5 : return queenMoves( theBoard:&theBoard, querySquare:theSquare, numMoves:numMoves, pieceColour:pieceColour )
			case -5 : return queenMoves( theBoard:&theBoard, querySquare:theSquare, numMoves:numMoves, pieceColour:pieceColour )
			case  6 : return wKingMoves( theBoard:&theBoard, querySquare:theSquare, numMoves:numMoves )
			case -6 : return bKingMoves( theBoard:&theBoard, querySquare:theSquare, numMoves:numMoves )
			
			//CASE abs(pieceType) = 5 // QUEEN TESTS
			//pieceMoves = fn bishopMoves( theBoard, moves(0), numMoves, querySquare, pieceColour )
			//pieceMoves += fn rookMoves( theBoard, moves(pieceMoves), numMoves, querySquare, pieceColour )
			//return pieceMoves
			
			default : pieceMoves = []
			
			}
		
		return pieceMoves
	}
	
	
	func generateRawMoves(theBoard: inout boardRecord) -> [moveRecord]
	{
		//var querySquare : Int
		//var numMoves : Int = 0
		//var pieceMoves : Int = 0
		var moveList : [moveRecord] = []
		
		// generate moves for all squares
		for querySquare in 0..<64 {
			
			// generate white moves
			if ( (theBoard.who2move == 1) && ((theBoard.square?[querySquare])! > 0) ) {
				moveList += pieceTree (theBoard: &theBoard, theSquare: querySquare)
				}
			
			// generate black moves
			if ( (theBoard.who2move == -1) && ((theBoard.square?[querySquare])! < 0) ) {
				moveList += pieceTree (theBoard: &theBoard, theSquare: querySquare)
				}
			
			}
		
		return moveList
	}
	
	
	func generateLegalMoves( theBoard: inout boardRecord ) -> [moveRecord]
	{
    var numRawMoves : Int = 0
	var numLegalMoves : Int = 0
	var testBoard : boardRecord
	var theMove : moveRecord
	var rawMoves : [moveRecord] = []
	var legalMoves : [moveRecord] = []
	
	rawMoves = generateRawMoves( theBoard: &theBoard )
	numRawMoves = rawMoves.count
	
	for n in 0..<numRawMoves {
		testBoard = theBoard
		theMove = rawMoves[n]
		
		makeMove( theBoard: &testBoard, theMove: theMove )
		
		if (isLegalPosition(theBoard: &testBoard)) {
			legalMoves.append(theMove)
			numLegalMoves += 1;
			}
		}
		
		return legalMoves
	}
	

	func generateCaptureMoves(theBoard: inout boardRecord) -> [moveRecord]
	{
		var attackedSquares : [Int] = []
		var captureMoves : [moveRecord] = []
		var querySquare, pieceType, pieceColour : Int
		
		for querySquare in 0..<64 {
			
			pieceColour = theBoard.who2move
			pieceType = (theBoard.square?[querySquare])!
			
			if ((pieceType == 1) && (pieceColour == 1)) {
				attackedSquares = wPawnAttacks( theBoard: &theBoard, querySquare: querySquare, numMoves: 0)
				captureMoves = attacks2moves( theBoard: &theBoard, querySquare: querySquare, attackedSquares:attackedSquares)
				}
			
			if ((pieceType == -1) && (pieceColour == -1)) {
				attackedSquares = bPawnAttacks( theBoard: &theBoard, querySquare: querySquare, numMoves: 0)
				captureMoves += attacks2moves( theBoard: &theBoard, querySquare: querySquare, attackedSquares:attackedSquares)
				}
			
			if (pieceType * pieceColour == 2) {
				attackedSquares = bishopAttacks( theBoard: &theBoard, querySquare: querySquare, numMoves: 0, pieceColour: theBoard.who2move)
				captureMoves += attacks2moves( theBoard: &theBoard, querySquare: querySquare, attackedSquares:attackedSquares)
				}
			
			if (pieceType * pieceColour == 3) {
				attackedSquares = knightAttacks( theBoard: &theBoard, querySquare: querySquare, numMoves: 0, pieceColour: theBoard.who2move)
				captureMoves += attacks2moves( theBoard: &theBoard, querySquare: querySquare, attackedSquares:attackedSquares)
				}
			
			if (pieceType * pieceColour == 4) {
				attackedSquares = rookAttacks( theBoard: &theBoard, querySquare: querySquare, numMoves: 0, pieceColour: theBoard.who2move)
				captureMoves += attacks2moves( theBoard: &theBoard, querySquare: querySquare, attackedSquares:attackedSquares)
				}
			
			if (pieceType * pieceColour == 5) {
				attackedSquares = queenAttacks( theBoard: &theBoard, querySquare: querySquare, numMoves: 0, pieceColour: theBoard.who2move)
				captureMoves += attacks2moves( theBoard: &theBoard, querySquare: querySquare, attackedSquares:attackedSquares)
				}
			
			if ((pieceType == 6) && (pieceColour == 1)) {
				attackedSquares = wKingAttacks( theBoard: &theBoard, querySquare: querySquare, numMoves: 0)
				captureMoves = attacks2moves( theBoard: &theBoard, querySquare: querySquare, attackedSquares:attackedSquares)
				}
			
			if ((pieceType == -6) && (pieceColour == -1)) {
				attackedSquares = bKingAttacks( theBoard: &theBoard, querySquare: querySquare, numMoves: 0)
				captureMoves += attacks2moves( theBoard: &theBoard, querySquare: querySquare, attackedSquares:attackedSquares)
				}
			
			}
		
		return captureMoves
	}
	
	
	// adjunct to FN generateCaptureMoves()
	func attacks2moves(theBoard: inout boardRecord, querySquare: Int, attackedSquares: [Int]) -> [moveRecord]
	{
		var captureMoves : [moveRecord] = []
		let numMoves : Int = attackedSquares.count
		
		for j in 0..<numMoves {
			//find: doesnt return capturedPiece for enPassant!!!
			let theMove = moveRecord(fromSquare:querySquare, toSquare:attackedSquares[j], capturedPiece:theBoard.square![attackedSquares[j]])
			captureMoves.append(theMove)
			}
		
		return captureMoves
	}
	
	
	func makeMove(theBoard: inout boardRecord, theMove:moveRecord)
	{
		let y1 : Int = sq2Y(square: theMove.fromSquare!) + 1
		let y2 : Int = sq2Y(square: theMove.toSquare!) + 1
		
		var pieceType : Int = theBoard.square![ theMove.fromSquare! ]
		
		// KING CASTLING
		if (pieceType == 6) {
			
			if (theBoard.fischer == false) {
			
				// castling - white-kingSide
				if ( (theMove.fromSquare == 4) && (theMove.toSquare==6) && (theBoard.square?[4]==6) && (theBoard.square?[7]==4) ) {
					theBoard.square![theMove.toSquare!] = (theBoard.square![theMove.fromSquare!])
					theBoard.square?[theMove.fromSquare!] = 0
					theBoard.square?[5] = 4
					theBoard.square?[7] = 0
					theBoard.wCastleKside = false
					theBoard.wCastleQside = false
					theBoard.wHasCastled = true
					theBoard.who2move = -theBoard.who2move
					theBoard.halfmoves += 1
					//theBoard.hashCode = not(theBoard.hashCode)
					gMovesMade += 1
					return
					}

				// castling - white-queenSide
				if ( (theMove.fromSquare == 4) && (theMove.toSquare==2) && (theBoard.square?[4]==6) && (theBoard.square?[0]==4) ) {
					theBoard.square![theMove.toSquare!] = (theBoard.square![theMove.fromSquare!])
					theBoard.square?[theMove.fromSquare!] = 0
					theBoard.square?[3] = 4
					theBoard.square?[0] = 0
					theBoard.wCastleKside = false
					theBoard.wCastleQside = false
					theBoard.wHasCastled = true
					theBoard.who2move = -theBoard.who2move
					theBoard.halfmoves += 1
					//theBoard.hashCode = not(theBoard.hashCode)
					gMovesMade += 1
					return;
					}
				}
			
			// FISCHER wKing
			if (theBoard.fischer == true) {
				
				// fischer castling - white-kingSide
				if ((theBoard.square?[theMove.fromSquare!])! == 6) && (theBoard.square?[theMove.toSquare!] == 4) && (theMove.toSquare == theBoard.rookSq2) {
					theBoard.square?[theMove.fromSquare!] = 0
					theBoard.square?[theBoard.rookSq2!] = 0
					theBoard.square?[6] = 6
					theBoard.square?[5] = 4
					theBoard.wCastleKside = false  //can no longer castle
					theBoard.wCastleQside = false  //can no longer castle
					theBoard.wHasCastled = true
					theBoard.who2move = -theBoard.who2move
					theBoard.halfmoves += 1
					gMovesMade += 1
					return
					}
				
				// fischer castling - white-queenSide
				if ((theBoard.square?[theMove.fromSquare!])! == 6) && (theBoard.square?[theMove.toSquare!] == 4) && (theMove.toSquare == theBoard.rookSq1) {
					theBoard.square?[theMove.fromSquare!] = 0
					theBoard.square?[theBoard.rookSq1!] = 0
					theBoard.square?[2] = 6
					theBoard.square?[3] = 4
					theBoard.wCastleKside = false  //can no longer castle
					theBoard.wCastleQside = false  //can no longer castle
					theBoard.wHasCastled = true
					theBoard.who2move = -theBoard.who2move
					theBoard.halfmoves += 1
					gMovesMade += 1
					return
					}
				
				}  //end wfischer==true
			
			}  //end wKing Castling
		
		
		if (pieceType == -6) {
			
			if (theBoard.fischer == false) {
				// castling - black-kingSide
				if ( (theMove.fromSquare == 60) && (theMove.toSquare==62) && (theBoard.square?[60] == -6) && (theBoard.square?[63] == -4) ) {
					theBoard.square![theMove.toSquare!] = (theBoard.square![theMove.fromSquare!])
					theBoard.square?[theMove.fromSquare!] = 0
					theBoard.square?[61] = -4
					theBoard.square?[63] = 0
					theBoard.bCastleKside = false
					theBoard.bCastleQside = false
					theBoard.bHasCastled = true
					theBoard.who2move = -theBoard.who2move
					theBoard.halfmoves += 1
					//theBoard.hashCode = not(theBoard.hashCode)
					gMovesMade += 1
					return;
					}
				
				// castling - black-queenSide
				if ( (theMove.fromSquare == 60) && (theMove.toSquare==58) && (theBoard.square?[60] == -6) && (theBoard.square?[56] == -4) ) {
					
					theBoard.square![theMove.toSquare!] = (theBoard.square![theMove.fromSquare!])
					theBoard.square?[theMove.fromSquare!] = 0
					theBoard.square?[59] = -4
					theBoard.square?[56] = 0
					theBoard.bCastleKside = false
					theBoard.bCastleQside = false
					theBoard.bHasCastled = true
					theBoard.who2move = -theBoard.who2move
					theBoard.halfmoves += 1
					//theBoard.hashCode = not(theBoard.hashCode)
					gMovesMade += 1
					return
					}
				}
			
			// FISCHER bKing
			if (theBoard.fischer == true) {
				
				// fischer castling - black-kingSide
				if ((theBoard.square?[theMove.fromSquare!])! == -6) && ((theBoard.square?[theMove.toSquare!])! == -4) && (theMove.toSquare == theBoard.rookSq2!+56) {
					theBoard.square?[theMove.fromSquare!] = 0
					theBoard.square?[theBoard.rookSq2! + 56] = 0
					theBoard.square?[62] = -6
					theBoard.square?[61] = -4
					theBoard.bCastleKside = false  //can no longer castle
					theBoard.bCastleQside = false  //can no longer castle
					theBoard.bHasCastled = true
					theBoard.who2move = -theBoard.who2move
					theBoard.halfmoves += 1
					gMovesMade += 1
					return
					}
				
				// fischer castling - black-queenSide
				//if (theMove.fromSquare == 60) && (theMove.toSquare == 58) && (theBoard.square?[60] == -6) && (theBoard.square?[56] == -4) {
					if ((theBoard.square?[theMove.fromSquare!])! == -6) && ((theBoard.square?[theMove.toSquare!])! == -4) && (theMove.toSquare == theBoard.rookSq1!+56) {
						theBoard.square?[theMove.fromSquare!] = 0
						theBoard.square?[theBoard.rookSq1! + 56] = 0
						theBoard.square?[58] = -6
						theBoard.square?[59] = -4
						theBoard.bCastleKside = false  //can no longer castle
						theBoard.bCastleQside = false  //can no longer castle
						theBoard.bHasCastled = true
						theBoard.who2move = -theBoard.who2move
						theBoard.halfmoves += 1
						gMovesMade += 1
						return
						}
				//	}
				
				}  //end bfischer==true
			
			}  // end bKing Castling
		
		
		// PAWN ENPASSANT
		if (pieceType == 1) {
			
			//test and take White enPassant West
			if ( (theMove.fromSquare! > 32) && (theMove.fromSquare! < 40) ) {
				if ( ((theBoard.square?[theMove.fromSquare!-1])! == -1) && (theMove.toSquare! - theMove.fromSquare! == 7) && (theMove.fromSquare!-1 == theBoard.enPassantSq) ) {
					
					theBoard.square?[theMove.fromSquare!-1] = 0
					theBoard.square![theMove.toSquare!] = (theBoard.square![theMove.fromSquare!])
					theBoard.square?[theMove.fromSquare!] = 0
					theBoard.who2move = -theBoard.who2move
					theBoard.enPassantSq = noSuchSquare
					theBoard.halfmoves += 1
					//theBoard.hashCode = not(theBoard.hashCode)
					gMovesMade += 1
					return
					}
				}
			
			//test and take White enPassant East
			if ( (theMove.fromSquare! > 31) && (theMove.fromSquare! < 39) ) {
				if ( ((theBoard.square?[theMove.fromSquare!+1])! == -1) && (theMove.toSquare! - theMove.fromSquare! == 9) && (theMove.fromSquare!+1 == theBoard.enPassantSq) ) {
					
					theBoard.square?[theMove.fromSquare!+1] = 0
					theBoard.square![theMove.toSquare!] = (theBoard.square![theMove.fromSquare!])
					theBoard.square?[theMove.fromSquare!] = 0
					theBoard.who2move = -theBoard.who2move
					theBoard.enPassantSq = noSuchSquare
					theBoard.halfmoves += 1
					//theBoard.hashCode = not(theBoard.hashCode)
					gMovesMade += 1
					return
					}
				}		
			}
		
		if (pieceType == -1) {
			
			//test and take Black enPassant West
			if ( (theMove.fromSquare! > 23) && (theMove.fromSquare! < 31) ) {
				if ( (theBoard.square![theMove.fromSquare!+1] == 1) && (theMove.fromSquare! - theMove.toSquare! == 7) && (theMove.fromSquare!+1 == theBoard.enPassantSq) ) {
					
					theBoard.square?[theMove.fromSquare!+1] = 0
					theBoard.square![theMove.toSquare!] = (theBoard.square![theMove.fromSquare!])
					theBoard.square?[theMove.fromSquare!] = 0
					theBoard.who2move = -theBoard.who2move
					theBoard.enPassantSq = noSuchSquare
					theBoard.halfmoves += 1
					//theBoard.hashCode = not(theBoard.hashCode)
					gMovesMade += 1
					return
					}
				}

			//test and take Black enPassant East
			if ( (theMove.fromSquare! > 24) && (theMove.fromSquare! < 32) ) {
				if ( ((theBoard.square?[theMove.fromSquare!-1])! == 1) && (theMove.fromSquare! - theMove.toSquare! == 9) && (theMove.fromSquare!-1 == theBoard.enPassantSq) ) {
					
					theBoard.square?[theMove.fromSquare!+1] = 0
					theBoard.square![theMove.toSquare!] = (theBoard.square![theMove.fromSquare!])
					theBoard.square?[theMove.fromSquare!] = 0
					theBoard.who2move = -theBoard.who2move
					theBoard.enPassantSq = noSuchSquare
					theBoard.halfmoves += 1
					//theBoard.hashCode = not(theBoard.hashCode)
					gMovesMade += 1
					return
					}
				}		
			}
		
		
		//--Make the ACTUAL MOVE--//
		theBoard.square![theMove.toSquare!] = (theBoard.square![theMove.fromSquare!])
		theBoard.square?[theMove.fromSquare!] = 0
		theBoard.who2move = -theBoard.who2move
		theBoard.halfmoves += 1
		//theBoard.hashCode = not(theBoard.hashCode)
		gMovesMade += 1		//because we're keeping count
		
		// if either King or Rook has moved, we can no longer castle
		if pieceType == 6 {theBoard.wHasCastled = false; theBoard.wCastleKside = false; theBoard.wCastleQside = false}
		if pieceType == -6 {theBoard.wHasCastled = false; theBoard.bCastleKside = false; theBoard.bCastleQside = false}
		
		if (!theBoard.fischer!) {
			if ( (pieceType == 4) && (theMove.fromSquare == 0) ) { theBoard.wCastleQside = false }
			if ( (pieceType == 4) && (theMove.fromSquare == 7) ) { theBoard.wCastleKside = false }
			if ( (pieceType == -4) && (theMove.fromSquare == 56) ) { theBoard.bCastleQside = false }
			if ( (pieceType == -4) && (theMove.fromSquare == 63) ) { theBoard.bCastleKside = false }
			}
		
		// fischer castling expiration!!
		if (theBoard.fischer)! {
			if (pieceType == 4) && (theMove.fromSquare == theBoard.rookSq1) {theBoard.wCastleQside = false}
			if (pieceType == 4) && (theMove.fromSquare == theBoard.rookSq2) {theBoard.wCastleKside = false}
			if (pieceType == -4) && (theMove.fromSquare == theBoard.rookSq1!+56) {theBoard.bCastleQside = false}
			if (pieceType == -4) && (theMove.fromSquare == theBoard.rookSq2!+56) {theBoard.bCastleKside = false}
			}
		
		// Pawn to Queen Promotion (queen always assumed)
		if ( (pieceType == 1) && (y1 == 7) && (y2 == 8) ) {
			theBoard.square?[theMove.toSquare!] = 5
			gPromotion = true
			}
		if ( (pieceType == -1) && (y1 == 2) && (y2 == 1) ) {
			theBoard.square?[theMove.toSquare!] = -5
			gPromotion = true
			}
		
		//  SET enPassant (into the new board)
		theBoard.enPassantSq = noSuchSquare
		if ( (pieceType == 1) && (y1 == 2) && (y2 == 4) ) {theBoard.enPassantSq = theMove.toSquare }
		if ( (pieceType == -1) && (y1 == 7) && (y2 == 5) ) {theBoard.enPassantSq = theMove.toSquare }
		
		return
	}
	
	
	// thanks to Martin.R from germany for Swift Insertion Sort
	func sortMoves( theMoves: inout [moveRecord])
	{
		for j in 1..<theMoves.count {
			let tempMove : moveRecord = theMoves[j]
			var i = j-1
			// compare abs() values or else black and white sort differently
			while i >= 0 && abs(theMoves[i].capturedPiece!) < abs(tempMove.capturedPiece!) {
				theMoves[i+1] = theMoves[i]
				i -= 1
				}
			theMoves[i+1] = tempMove
			}
		return
	}
	
	
	func putSuppliedMoveFirst( theMoves: inout [moveRecord], bestIndex: Int)
	{
		let bestVal = theMoves[bestIndex]
		theMoves.remove(at: bestIndex)
		theMoves.insert(bestVal, at:0)
		return
	}
	
	
	func isAttacked( theBoard : inout boardRecord, querySquare: Int) -> Bool
	{
		var attacked : Bool = false
		var numMoves : Int = 0
		var attackedSquares : [Int] = []
		
		let pieceType = theBoard.square?[querySquare]
		
		for sq in 0...63 {
			
			// white pawn attacks
			if ( (theBoard.square?[sq] == 1) && (theBoard.who2move == 1) ) {
				var attackedSquares = wPawnAttacks( theBoard:&theBoard, querySquare: sq, numMoves: 0 )
				for n in 0..<attackedSquares.count {
					if (attackedSquares[n] == querySquare) {
						attacked = true
						return attacked
						}
					}
				}
			
			// black pawn attacks
			if ( (theBoard.square?[sq] == -1) && (theBoard.who2move == -1) ) {
				var attackedSquares = bPawnAttacks( theBoard:&theBoard, querySquare: sq, numMoves: 0 )
				for n in 0..<attackedSquares.count {
					if (attackedSquares[n] == querySquare) {
						attacked = true
						return attacked
						}
					}
				}
			
			// bishop attacks
			if ((theBoard.square?[sq])! * theBoard.who2move == 2) {
				var attackedSquares = bishopAttacks( theBoard:&theBoard, querySquare: sq, numMoves: 0, pieceColour:theBoard.who2move )
				for n in 0..<attackedSquares.count {
					if (attackedSquares[n] == querySquare) {
						attacked = true
						return attacked
						}
					}
				}
			
			// knight attacks
			if ((theBoard.square?[sq])! * theBoard.who2move == 3) {
				var attackedSquares = knightAttacks( theBoard:&theBoard, querySquare: sq, numMoves: 0, pieceColour:theBoard.who2move )
				for n in 0..<attackedSquares.count {
					if (attackedSquares[n] == querySquare) {
						attacked = true
						return attacked
						}
					}
				}
			
			// rook attacks
			if ((theBoard.square?[sq])! * theBoard.who2move == 4) {
				var attackedSquares = rookAttacks( theBoard:&theBoard, querySquare: sq, numMoves: 0, pieceColour:theBoard.who2move )
				for n in 0..<attackedSquares.count {
					if (attackedSquares[n] == querySquare) {
						attacked = true
						return attacked
						}
					}
				}
			
			// queen attacks
			if ((theBoard.square?[sq])! * theBoard.who2move == 5) {
				var attackedSquares = queenAttacks( theBoard:&theBoard, querySquare: sq, numMoves: 0, pieceColour:theBoard.who2move )
				for n in 0..<attackedSquares.count {
					if (attackedSquares[n] == querySquare) {
						attacked = true
						return attacked
						}
					}
				}
			
			// wKingAttacks
			if ( (theBoard.square?[sq] == 6) && (theBoard.who2move == 1) ) {
				var attackedSquares = wKingAttacks( theBoard:&theBoard, querySquare: sq, numMoves: 0 )
				for n in 0..<attackedSquares.count {
					if (attackedSquares[n] == querySquare) {
						attacked = true
						return attacked
						}
					}
				}
			
			// bKingAttacks
			if ( (theBoard.square?[sq] == -6) && (theBoard.who2move == -1) ) {
				var attackedSquares = bKingAttacks( theBoard:&theBoard, querySquare: sq, numMoves: 0 )
				for n in 0..<attackedSquares.count {
					if (attackedSquares[n] == querySquare) {
						attacked = true
						return attacked
						}
					}
				}
			
			}  // if we made it this far, we have not been attacked! :-D
		
		return attacked
	}
	
	
	func isSideToMoveInCheck(theBoard: inout boardRecord) -> Bool
	{
		var myKingSq : Int
		var inCheck : Bool = false
		
		//for (myKingSq = 0; myKingSq < 64; myKingSq++) {
		for myKingSq in 0..<64 {
			if ( theBoard.square![myKingSq] * theBoard.who2move == 6 ) {
				//cout << "kingSq: " << myKingSq << endl;
				theBoard.who2move = -theBoard.who2move
				inCheck = isAttacked(theBoard: &theBoard, querySquare: myKingSq )
				theBoard.who2move = -theBoard.who2move
				}
			}
		
		// if no king, we can not be in check (allows debugging minus king values)
		return inCheck
	}
	
	
	func isLegalPosition(theBoard: inout boardRecord) -> Bool
	{
		var oppKingSq : Int
		var legal : Bool = true
		
		// false means that the last move was illegal.
		// i.e. the side to move can capture opposing K
		
		for oppKingSq in 0..<64 {
			if ( theBoard.square![oppKingSq] * theBoard.who2move == -6 ) {
				legal = ( isAttacked(theBoard: &theBoard, querySquare: oppKingSq) == false )
				}
			}
		
		return legal
	}
	
	
	// used ONLY by algeb2Std() for disambiguation (therefore not optimized for performance)
	func isTarget(toSquare:Int, inBoard: inout boardRecord, pieceType: String) -> [moveRecord]
	{
		var targetMoves : [moveRecord] = []
		var numTargetMoves : Int = 0
		var n, sq, pieceColour : Int
		var numMoves : Int = 0
		var moves : [moveRecord] = []
		var theBoard : boardRecord
		
		theBoard = inBoard
		pieceColour = theBoard.who2move
		
		// generate all the emoves (or just for a specific pieceType)
		for sq in 0..<64 {
			
			// white pawn targets
			if ( (theBoard.square?[sq] == 1) && (pieceColour == 1) && ((pieceType == "") || (pieceType == "P")) ) {
				moves += wPawnMoves( theBoard: &theBoard, querySquare: sq, numMoves: numMoves )
				numMoves = moves.count
				}
			
			// black pawn targets
			if ( (theBoard.square?[sq] == -1) && (pieceColour == -1) && ((pieceType == "") || (pieceType == "P")) ) {
				moves += bPawnMoves( theBoard: &theBoard, querySquare: sq, numMoves: numMoves )
				numMoves = moves.count
				}
			
			// bishop targets
			if ( ((theBoard.square?[sq])! * theBoard.who2move == 2) && ((pieceType == "") || (pieceType == "B")) ) {
				moves += bishopMoves( theBoard: &theBoard, querySquare: sq, numMoves: numMoves, pieceColour: pieceColour )
				numMoves = moves.count
				}
			
			// knight targets
			if ( ((theBoard.square?[sq])! * theBoard.who2move == 3) && ((pieceType == "") || (pieceType == "N")) ) {
				moves += knightMoves( theBoard: &theBoard, querySquare: sq, numMoves:numMoves, pieceColour: pieceColour )
				numMoves = moves.count
				}
			
			// rook targets
			if ( ((theBoard.square?[sq])! * theBoard.who2move == 4) && ((pieceType == "") || (pieceType == "R")) ) {
				moves += rookMoves( theBoard: &theBoard, querySquare: sq, numMoves: numMoves, pieceColour: pieceColour )
				numMoves = moves.count
				}
			
			// queen targets
			if ( ((theBoard.square?[sq])! * theBoard.who2move == 5) && ((pieceType == "") || (pieceType == "Q")) ) {
				moves += queenMoves( theBoard: &theBoard, querySquare: sq, numMoves: numMoves, pieceColour: pieceColour )
				numMoves = moves.count
				}
			
			// white king targets
			if ( (theBoard.square?[sq] == 6) && (pieceColour == 1) && ((pieceType == "") || (pieceType == "K")) ) {
				moves += wKingMoves( theBoard: &theBoard, querySquare: sq, numMoves: numMoves )
				numMoves = moves.count
				}
		
			// black king targets
			if ( (theBoard.square?[sq] == -6) && (pieceColour == -1) && ((pieceType == "") || (pieceType == "K")) ) {
				moves += bKingMoves( theBoard: &theBoard, querySquare: sq, numMoves: numMoves )
				numMoves = moves.count
				}
			
			}  //end for sq in..
		
		
		// once we've made all the moves, see if any of them hit the target!
		for n in 0..<numMoves {
			if ( moves[n].toSquare == toSquare ) {
				let theMove = moveRecord(fromSquare:moves[n].fromSquare, toSquare:moves[n].toSquare, capturedPiece:theBoard.square![ moves[n].toSquare!])
				targetMoves.append(theMove)
				numTargetMoves += 1
				}
			}
		
		return targetMoves
	}
	
	
	
