	func quiescenceSearch(theBoard: inout boardRecord, alpha: Int, beta: Int, depth: Int) -> Int
	{
		var alphaScore : Int = alpha
		var betaScore : Int = beta
		
		var numMoves, moveIndex, moveScore : Int
		var testBoard : boardRecord
		var theMove : moveRecord
		var captureMoves : [moveRecord]
		
		var patScore : Int = staticEval( theBoard: &theBoard )
		
		if depth < minQSearchDepth {
			alphaScore = patScore
			return alphaScore
			}
		
		if patScore > alphaScore {
			alphaScore = patScore
			// beta cutoff
			if alphaScore >= betaScore {return alphaScore}
			}
		
		captureMoves = generateCaptureMoves( theBoard: &theBoard )
		numMoves = captureMoves.count
		
		if numMoves == 0 {return patScore}
		
		sortMoves( theMoves: &captureMoves )
		
		for moveIndex in 0..<numMoves {
			testBoard = theBoard
			theMove = captureMoves[moveIndex]
			makeMove( theBoard: &testBoard, theMove: theMove )
			
			if ( isLegalPosition(theBoard: &testBoard) ) {
				gqNodes += 1
				moveScore = -quiescenceSearch( theBoard: &testBoard, alpha:-betaScore, beta:-alphaScore, depth:depth-1 )
				
				if moveScore > alphaScore {
					alphaScore = moveScore
					//beta cutoff
					if alphaScore >= betaScore {
						return alphaScore
						}
					}
				}
			}
		
		return alphaScore
	}
	
	
	//---=< NEGAMAX >=---//
	
	func negaSearch(theBoard: inout boardRecord, alpha:Int, beta:Int, depth:Int, ply:Int) -> Int
	{
		var alphaScore : Int = alpha
		var betaScore : Int = beta
		var rawMoves : [moveRecord]
		var numRawMoves, moveIndex : Int
		var moveScore : Int
		var numLegal : Int = 0
		var moves : [moveRecord]
		var theMove : moveRecord
		var testBoard : boardRecord
		
		// draw by 50; insufficient strength; or repetition > return 0 here
		
		// check for timed abortSearch
		//if (fn PollTime) and (depth > 2) then fn CheckForAbortSignal // ESC Key Pressed?
		//if (fn PollTime2) then fn SetAbortSearch // Timer Ran Out
		
		// JRP: if (isSearchAborted) {break}  ??is this correct??
		if shouldTerminate == true { 
			alphaScore = quiescenceSearch(theBoard:&theBoard, alpha:alphaScore, beta:betaScore, depth:depth-1)
			return alphaScore
			}
				
		// if depth<=0 then quiescenceSearch
		if depth <= 0 {
			if (!quiescence) { alphaScore = staticEval(theBoard: &theBoard) }
			if (quiescence) { alphaScore = quiescenceSearch(theBoard:&theBoard, alpha:alphaScore, beta:betaScore, depth:depth-1) }
			return alphaScore
			}
		
		rawMoves = generateRawMoves( theBoard: &theBoard )
		numRawMoves = rawMoves.count
		
		sortMoves( theMoves: &rawMoves )
		
		for moveIndex in 0..<numRawMoves {
			testBoard = theBoard
			theMove = rawMoves[moveIndex]
			makeMove( theBoard: &testBoard, theMove: theMove )
			
			if (isLegalPosition(theBoard:&testBoard)) {
				numLegal += 1
				//gNodesSearched +=1
				
				moveScore = -negaSearch(theBoard: &testBoard, alpha:-betaScore, beta:-alphaScore, depth:depth-1, ply:ply+1)
				
				// purves infoToGUI() debugrrr goes HERE
				if (depth > 4) {
					let dispMove = move2Algebraic( theMove: &theMove )
					infoToGUI(moveIndex: moveIndex, dispMove: dispMove, depth: depth, moveScore: moveScore, nodes: gMovesMade)
					} 
				
				//if moveScore > alphaScore {alphaScore = moveScore; theMove.score = moveScore}
				if moveScore > alphaScore {alphaScore = moveScore}
				
				// alpha-beta cutoff
				if alphaScore >= betaScore {break}
				
				}
			}
		
		// no legal moves
		if numLegal==0 {
			if (isSideToMoveInCheck(theBoard:&theBoard)) {
				alphaScore = iAmMateScore + ply
				return alphaScore
				}
			else {
				alphaScore = stalemateScore
				return alphaScore
				}
			}
		
		return alphaScore
	}
	

