			return theMove
			}
		
		if numMoves == 1 {maxDepth = 1}		// dont go deep if only one move
		
		sortMoves( theMoves: &moves )		// put captures first
		
		//initHashTable
		
		
		//---| DEPTHS |---//
		
		for iDepth in 1...maxDepth {
			
			//-print("DEPTH \(iDepth) ")
			
			alphaScore = -infinity
			
			
			//---| MOVES |---//
			
			for moveIndex in 0..<numMoves {
				
				testBoard = theBoard
				theMove = moves[moveIndex]
				makeMove(theBoard:&testBoard, theMove: theMove)
				
				// search with beta fixed at -_infinity
				moveScore = -negaSearch(theBoard: &testBoard, alpha: -infinity, beta: -alphaScore, depth: iDepth, ply: 1)
				
