	func bishopAttacks( theBoard: inout boardRecord, querySquare:Int, numMoves:Int, pieceColour:Int ) -> [Int]
	{
		var bishopMoves : [Int] = []
		var pieceHit : Int = 0
		var pieceMoves : Int = 0
		var moveIndex : Int = 0
		
		var testMask : Int
		var newMask : Int
		var testSquare : Int = 0
		
		for vectorCount in 0...3 {
			pieceHit = 0
			testSquare = querySquare
			
			repeat {
				// hit ourselves first
				if ( (pieceHit < 2) && ((theBoard.square![testSquare] * pieceColour) > 0) ) {
					pieceHit += 1
					}
				
				// bishop capture (terminal end of vector)
				if ( (pieceHit < 2) && ((theBoard.square![testSquare] * pieceColour) < 0) ) {
					pieceHit += 1
					moveIndex = numMoves + pieceMoves
					let theMove = moveRecord(fromSquare:querySquare, toSquare:testSquare, capturedPiece:theBoard.square![testSquare])
					bishopMoves.append(testSquare)
					pieceMoves += 1
					}
				
				//set the next bishap vector and Do the Doodle Doo..
				testMask = sq2Mask[testSquare]
				newMask = testMask + gBishopMask[vectorCount]
				testSquare = testSquare + gBishopVect[vectorCount]
				
				//until (piecehit=2) or (maskBoard(newMask) = -1 )
				} while !( !(pieceHit < 2) || !(gMaskBoard[newMask] == 1) )
			
			}
		
		return bishopMoves
	}
	
	
