	func perft( inBoard: inout boardRecord, depth: Int ) -> Int
	{
		var board : boardRecord
		var nodes : Int = 0
		var j : Int
		
		let moves : [moveRecord] = generateLegalMoves( theBoard: &inBoard )
		//let moves : [moveRecord] = generateRawMoves( theBoard: &inBoard )
		let numLegal = moves.count
		
		if (depth == 1 ) { nodes = moves.count }
	    else {
	        for j in 0..<numLegal {
				board = inBoard
				makeMove( theBoard:&board, theMove:moves[j] )
				nodes += perft( inBoard: &board, depth: depth-1 )
				}
			}
		
	    return nodes;
	}
	
