				let squareVal = theBoard.square![querySquare]
		
				if squareVal < -6 || squareVal > 6 { squareStr = "-" }
				
				if squareVal == 0 { squareStr = "-" }
				if squareVal == 1 { squareStr = "P" }
				if squareVal == 2 { squareStr = "B" }
				if squareVal == 3 { squareStr = "N" }
				if squareVal == 4 { squareStr = "R" }
				if squareVal == 5 { squareStr = "Q" }
				if squareVal == 6 { squareStr = "K" }
				if squareVal == -1 { squareStr = "p" }
				if squareVal == -2 { squareStr = "b" }
				if squareVal == -3 { squareStr = "n" }
				if squareVal == -4 { squareStr = "r" }
				if squareVal == -5 { squareStr = "q" }
				if squareVal == -6 { squareStr = "k" }
				
				line = line + " " + squareStr
				}
			
			line = "|" + line + " | " + "\(i+1)"
			boardString = boardString + line + "\n"
			}
		
			boardString += "+--+-+-+-+-+-+-+--+--\n"
			boardString += "| a b c d e f g h | "
		
			if theBoard.who2move == 1 { boardString += "W" }
			if theBoard.who2move == -1 { boardString += "B" }

			if !(theBoard.fischer!) {
				propStr = "| EP:\(theBoard.enPassantSq!) "
				if (theBoard.wHasCastled)! {propStr += "W+"} else {propStr += "W-"}
				if (theBoard.bHasCastled)! {propStr += "B+ "} else {propStr += "B- "}
				if (theBoard.wCastleKside)! {propStr += "K"} else {propStr += "-"}
				if (theBoard.wCastleQside)! {propStr += "Q"} else {propStr += "-"}
				if (theBoard.bCastleKside)! {propStr += "k"} else {propStr += "-"}
				if (theBoard.bCastleQside)! {propStr += "q "} else {propStr += "- "}
				if (theBoard.fischer)! {propStr += "FISH💚 "} else {propStr += "FISH💔 "}
				propStr += "r1:\(theBoard.rookSq1!) r2:\(theBoard.rookSq2!)"
				}
				
			else {
				let r1str : String = getChar(theString:"ABCDEFGH", charIndex:theBoard.rookSq1!)
				let r2str : String = getChar(theString:"ABCDEFGH", charIndex:theBoard.rookSq2!)
				let r3str : String = getChar(theString:"abcdefgh", charIndex:theBoard.rookSq1!)
				let r4str : String = getChar(theString:"abcdefgh", charIndex:theBoard.rookSq2!)
				
				propStr = "| EP:\(theBoard.enPassantSq!) "
				if (theBoard.wHasCastled)! {propStr += "W+"} else {propStr += "W-"}
				if (theBoard.bHasCastled)! {propStr += "B+ "} else {propStr += "B- "}
				if (theBoard.wCastleQside)! {propStr += r1str} else {propStr += "-"}
				if (theBoard.wCastleKside)! {propStr += r2str} else {propStr += "-"}
				if (theBoard.bCastleQside)! {propStr += r3str} else {propStr += "-"}
				if (theBoard.bCastleKside)! {propStr += r4str} else {propStr += "- "}
				if (theBoard.fischer)! {propStr += " FISH💚 "} else {propStr += " FISH💔 "}
				propStr += "r1:\(theBoard.rookSq1!) r2:\(theBoard.rookSq2!)"
				}
		
			boardString = boardString + "\n" + propStr
		
		return boardString
	}
	
	// end of peaBrain Class
}


